package com.syntax.class2;

public class ShortHandOperatorJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=10;
number=number+2;
System.out.println(number);
number++;
System.out.println(number);
System.out.println("**************");
number=10;
number=number+30;
System.out.println(number);
number+=30;//number=number+30;
System.out.println(number);
number=10;
number-=5;
System.out.println(number);
System.out.println("********************");
number=10;
number/=5;
System.out.println(number);
number=10;
number%=5;
System.out.println(number);

	}

}
